     9→import { Client } from "@modelcontextprotocol/sdk/client/index.js";
    10→import { StdioClientTransport } from "@modelcontextprotocol/sdk/client/stdio.js";
    11→
    12→const __filename = fileURLToPath(import.meta.url);
    13→const __dirname = path.dirname(__filename);
    14→const projectRoot = path.resolve(__dirname, "..");
    15→const artifactDir = path.join(projectRoot, ".tmp-test-artifacts");
    16→
    17→async function withClient(run) {
    18→  const client = new Client({ name: "baepsae-real-test", version: "1.0.0" });
    19→  const transport = new StdioClientTransport({
    20→    command: "node",
    21→    args: ["dist/index.js"],
    22→    cwd: projectRoot,
    23→    stderr: "pipe",
    24→  });
    25→
    26→  await client.connect(transport);
    27→  try {
    28→    return await run(client);
    29→  } finally {
    30→    await client.close();
    31→  }
    32→}
    33→
    34→function extractText(result) {
    35→  return result.content
    36→    .filter((item) => item.type === "text")
    37→    .map((item) => item.text)
    38→    .join("\n");
    39→}
    40→
    41→function extractBootedUdid(text) {
    42→  const match = text.match(/\(([0-9A-F-]{36})\)\s+\(Booted\)/i);
    43→  return match ? match[1] : null;
    44→}
    45→
    46→function isAccessibilityDenied(text) {
    47→  return /\[Permission Denied\].*Accessibility/i.test(text);
    48→}
    49→
    50→const sleep = (ms) => new Promise((r) => setTimeout(r, ms));
    51→
    52→/**
    53→ * Poll analyze_ui until check(text) returns true or timeout.
    54→ * Returns the last analyze_ui text.
    55→ */
    56→async function waitForUI(client, udid, focusId, check, timeoutMs = 5000) {
    57→  const interval = 500;
    58→  const deadline = Date.now() + timeoutMs;
    59→  let lastText = "";
    60→  while (Date.now() < deadline) {
    61→    const args = { udid };
    62→    if (focusId) args.focusId = focusId;
    63→    const result = await client.callTool({ name: "analyze_ui", arguments: args });
    64→    lastText = extractText(result);
    65→    if (!result.isError && check(lastText)) return lastText;
    66→    await sleep(interval);
    67→  }
    68→  return lastText;
    69→}
    70→
    71→/**
    72→ * Detect if a context menu (Cut/Copy/Paste) is visible in the UI.
    73→ */
    74→async function hasContextMenu(client, udid) {
    75→  const result = await client.callTool({ name: "analyze_ui", arguments: { udid } });
    76→  const text = extractText(result);
    77→  return /\b(Cut|Copy|Paste|Select All|Select)\b/.test(text);
    78→}
    79→
    80→/**
    81→ * Dismiss context menu if present by tapping outside or sending Escape.
    82→ * Returns true if a menu was detected and dismissed.
    83→ */
    84→async function dismissContextMenu(client, udid) {
    85→  if (!(await hasContextMenu(client, udid))) return false;
    86→  // Send Escape key (keycode 41 in HID) to dismiss
    87→  await client.callTool({ name: "key", arguments: { udid, keycode: 41 } });
    88→  await sleep(500);
    89→  // If still present, tap an empty area
    90→  if (await hasContextMenu(client, udid)) {
    91→    await client.callTool({ name: "tap", arguments: { udid, x: 200, y: 50 } });
    92→    await sleep(500);
    93→  }
    94→  return true;
    95→}
