    97→function findSampleApp() {
    98→  const candidates = [
    99→    path.join(projectRoot, "test-fixtures", "SampleApp", "build", "Debug-iphonesimulator", "SampleApp.app"),
   100→    path.join(projectRoot, "test-fixtures", "SampleApp", "Build", "Products", "Debug-iphonesimulator", "SampleApp.app"),
   101→  ];
   102→
   103→  for (const candidate of candidates) {
   104→    if (existsSync(candidate)) {
   105→      return candidate;
   106→    }
   107→  }
   108→  return null;
   109→}
