   111→const sampleAppProject = path.join(projectRoot, "test-fixtures", "SampleApp", "SampleApp.xcodeproj");
   112→
   113→// ─── Setup: Build and install SampleApp ──────────────────────────────────────
   114→
   115→test("Setup: build SampleApp for simulator", { timeout: 120_000 }, async (t) => {
   116→  if (!existsSync(sampleAppProject)) {
   117→    t.skip("SampleApp.xcodeproj not found");
   118→    return;
   119→  }
   120→
   121→  await withClient(async (client) => {
   122→    const listResult = await client.callTool({ name: "list_simulators", arguments: {} });
   123→    const udid = extractBootedUdid(extractText(listResult));
   124→    if (!udid) {
   125→      t.skip("No booted simulator detected");
   126→      return;
   127→    }
   128→
   129→    // Build SampleApp targeting the booted simulator
   130→    execSync(
   131→      `xcodebuild -project "${sampleAppProject}" -scheme SampleApp -sdk iphonesimulator ` +
   132→      `-destination "id=${udid}" build`,
   133→      { cwd: path.join(projectRoot, "test-fixtures", "SampleApp"), stdio: "pipe", timeout: 90_000 },
   134→    );
   135→
   136→    const appPath = findSampleApp();
   137→    assert.ok(appPath, "SampleApp.app should exist after build");
   138→
   139→    // Install and launch
   140→    const installResult = await client.callTool({
   141→      name: "install_app",
   142→      arguments: { udid, path: appPath },
   143→    });
   144→    assert.equal(installResult.isError ?? false, false, "install_app should not error");
   145→
   146→    const launchResult = await client.callTool({
   147→      name: "launch_app",
   148→      arguments: { udid, bundleId: "com.baepsae.sampleapp" },
   149→    });
   150→    assert.equal(launchResult.isError ?? false, false, "launch_app should not error");
   151→
   152→    // Wait for the app UI to load
   153→    await waitForUI(client, udid, "test-label", (text) => text.includes("Ready"), 10000);
   154→  });
   155→});
