   954→// ─── Phase 4: macOS app interaction (Safari) ────────────────────────────────
   955→// These tests require Safari and macOS accessibility permissions.
   956→// They are skipped in CI or when Safari is unavailable.
   957→
   958→const gestureFixturePath = path.join(projectRoot, "test-fixtures", "mac-gesture-test.html");
   959→
   960→function isMacOS() {
   961→  return process.platform === "darwin";
   962→}
   963→
   964→function isCI() {
   965→  return !!(process.env.CI || process.env.GITHUB_ACTIONS || process.env.JENKINS_URL);
   966→}
   967→
   968→async function isSafariAvailable() {
   969→  if (!isMacOS()) return false;
   970→  try {
   971→    execSync("pgrep -x Safari", { stdio: "pipe" });
   972→    return true;
   973→  } catch {
   974→    // Safari not running — try to open it
   975→    try {
   976→      execSync(`open -a Safari "${gestureFixturePath}"`, { stdio: "pipe", timeout: 5000 });
   977→      await sleep(3000);
   978→      return true;
   979→    } catch {
   980→      return false;
   981→    }
   982→  }
   983→}
   984→
   985→/**
   986→ * Poll query_ui for a macOS app until check(text) returns true or timeout.
   987→ */
   988→async function waitForMacUI(client, bundleId, query, check, timeoutMs = 5000) {
   989→  const interval = 500;
   990→  const deadline = Date.now() + timeoutMs;
   991→  let lastText = "";
   992→  while (Date.now() < deadline) {
   993→    const result = await client.callTool({
   994→      name: "query_ui",
   995→      arguments: { bundleId, query },
   996→    });
   997→    lastText = extractText(result);
   998→    if (!result.isError && check(lastText)) return lastText;
   999→    await sleep(interval);
  1000→  }
  1001→  return lastText;
  1002→}
