164→export async function executeCommand(
165→  executable: string,
166→  args: string[],
167→  options: CommandExecutionOptions = {}
168→): Promise<CommandExecutionResult> {
169→  const startedAt = Date.now();
170→  const timeoutMs = Math.max(1, options.timeoutMs ?? DEFAULT_TIMEOUT_MS);
171→  const captureStdout = options.captureStdout ?? !options.stdoutFilePath;
172→
173→  const outputPath = options.stdoutFilePath ? await ensureOutputPath(options.stdoutFilePath) : undefined;
174→
175→  return await new Promise<CommandExecutionResult>((resolvePromise, rejectPromise) => {
176→    const child = spawn(executable, args, {
177→      stdio: ["pipe", "pipe", "pipe"],
178→      env: options.env ? { ...process.env, ...options.env } : process.env,
179→    });
180→
181→    const stdoutChunks: Buffer[] = [];
182→    const stderrChunks: Buffer[] = [];
183→    let timedOut = false;
184→    let closed = false;
185→    let escalationTimer: NodeJS.Timeout | undefined;
186→    let outputFinished: Promise<void> | undefined;
187→
188→    if (outputPath) {
189→      const outputStream = createWriteStream(outputPath);
190→      outputFinished = new Promise<void>((resolveOutput, rejectOutput) => {
191→        outputStream.on("finish", () => resolveOutput());
192→        outputStream.on("error", (error) => rejectOutput(error));
193→      });
194→      child.stdout.pipe(outputStream);
195→    } else if (captureStdout) {
196→      child.stdout.on("data", (chunk: Buffer | string) => {
197→        stdoutChunks.push(Buffer.isBuffer(chunk) ? chunk : Buffer.from(chunk));
198→      });
199→    }
200→
201→    child.stderr.on("data", (chunk: Buffer | string) => {
202→      stderrChunks.push(Buffer.isBuffer(chunk) ? chunk : Buffer.from(chunk));
203→    });
204→
205→    child.on("error", (error) => {
206→      rejectPromise(new Error(`Failed to execute command: ${error.message}`));
207→    });
208→
209→    if (options.stdinText !== undefined) {
210→      child.stdin.write(options.stdinText);
211→    }
212→    child.stdin.end();
213→
214→    const timer = setTimeout(() => {
215→      if (closed) {
216→        return;
217→      }
218→
219→      timedOut = true;
220→      child.kill("SIGINT");
221→
222→      escalationTimer = setTimeout(() => {
223→        if (!closed) {
224→          child.kill("SIGTERM");
225→        }
226→      }, 800);
227→    }, timeoutMs);
228→
229→    child.on("close", async (exitCode, signal) => {
230→      closed = true;
231→      clearTimeout(timer);
232→      if (escalationTimer) {
233→        clearTimeout(escalationTimer);
234→      }
235→
236→      try {
237→        if (outputFinished) {
238→          await outputFinished;
239→        }
240→
241→        resolvePromise({
242→          executablePath: executable,
243→          args,
244→          stdout: captureStdout ? Buffer.concat(stdoutChunks).toString("utf8").trimEnd() : "",
245→          stderr: Buffer.concat(stderrChunks).toString("utf8").trimEnd(),
246→          exitCode,
247→          signal,
248→          timedOut,
249→          durationMs: Date.now() - startedAt,
250→        });
251→      } catch (error) {
252→        rejectPromise(error instanceof Error ? error : new Error(String(error)));
253→      }
254→    });
255→  });
256→}
