262→export function toToolResult(result: CommandExecutionResult, options: ResponseOptions = {}): ToolTextResult {
263→  const timeoutIsExpected = options.timeoutIsExpected ?? false;
264→  const expectedTimeoutSignal =
265→    timeoutIsExpected && result.timedOut && (result.signal === "SIGINT" || result.signal === "SIGTERM");
266→  const isError =
267→    (result.exitCode !== 0 && result.exitCode !== null) ||
268→    (result.timedOut && !timeoutIsExpected) ||
269→    (result.signal !== null && !expectedTimeoutSignal);
270→
271→  const lines: string[] = [
272→    `Executable: ${result.executablePath}`,
273→    `Command: ${commandToString(basename(result.executablePath), result.args)}`,
274→    `Exit code: ${result.exitCode === null ? "null" : String(result.exitCode)}`,
275→    `Duration: ${result.durationMs}ms`,
276→  ];
277→
278→  if (result.signal) {
279→    lines.push(`Signal: ${result.signal}`);
280→  }
281→
282→  if (result.timedOut) {
283→    lines.push(`Timed out: ${timeoutIsExpected ? "expected stop" : "yes"}`);
284→  }
285→
286→  if (options.extraLines && options.extraLines.length > 0) {
287→    lines.push(...options.extraLines);
288→  }
289→
290→  if (result.stdout.length > 0) {
291→    lines.push("", "STDOUT:", result.stdout);
292→  }
293→
294→  if (result.stderr.length > 0) {
295→    lines.push("", "STDERR:", result.stderr);
296→  }
297→
298→  return {
299→    content: [{ type: "text", text: lines.join("\n") }],
300→    isError,
301→  };
302→}
303→
304→async function runCommand(
305→  executable: string,
306→  args: string[],
307→  options?: CommandExecutionOptions,
308→  responseOptions?: ResponseOptions
309→): Promise<ToolTextResult> {
310→  try {
311→    const result = await executeCommand(executable, args, options);
312→    return toToolResult(result, responseOptions);
313→  } catch (error) {
314→    const message = error instanceof Error ? error.message : String(error);
315→    return {
316→      content: [{ type: "text", text: `Error executing ${commandToString(executable, args)}\n${message}` }],
317→      isError: true,
318→    };
319→  }
320→}
