326→export async function runNative(
327→  args: string[],
328→  options?: CommandExecutionOptions,
329→  responseOptions?: ResponseOptions
330→): Promise<ToolTextResult> {
331→  let binary: string;
332→  try {
333→    binary = resolveNativeBinary();
334→  } catch (error) {
335→    return {
336→      content: [{ type: "text", text: error instanceof Error ? error.message : String(error) }],
337→      isError: true,
338→    };
339→  }
340→  return await runCommand(binary, args, options, responseOptions);
341→}
