301→export function registerUITools(server: McpServer): void {
302→  const registerDescribeTool = (
303→    name: string,
304→    description: string,
305→    targetSchema: Record<string, z.ZodTypeAny>,
306→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
307→  ) => {
308→    server.tool(name, description, { ...targetSchema, ...describeSchema }, async (params) => {
309→      const target = resolveTarget(params as AnyTargetParams);
310→      if (!Array.isArray(target)) return target;
311→      return await runNative(buildDescribeArgs(target, params as DescribeParams));
312→    });
313→  };
314→
315→  const registerSearchTool = (
316→    name: string,
317→    description: string,
318→    targetSchema: Record<string, z.ZodTypeAny>,
319→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
320→  ) => {
321→    server.tool(name, description, { ...targetSchema, ...searchSchema }, async (params) => {
322→      const target = resolveTarget(params as AnyTargetParams);
323→      if (!Array.isArray(target)) return target;
324→      return await runNative(buildSearchArgs(target, params as SearchParams));
325→    });
326→  };
327→
328→  const registerTapTool = (
329→    name: string,
330→    description: string,
331→    targetSchema: Record<string, z.ZodTypeAny>,
332→    tapSchemaVariant: Record<string, z.ZodTypeAny>,
333→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
334→  ) => {
335→    server.tool(name, description, { ...targetSchema, ...tapSchemaVariant }, async (params) => {
336→      const validationError = validateTapParams(params as TapParams);
337→      if (validationError) return validationError;
338→
339→      const target = resolveTarget(params as AnyTargetParams);
340→      if (!Array.isArray(target)) return target;
341→      return await runNative(buildTapArgs(target, params as TapParams));
342→    });
343→  };
344→
345→  const registerTypeTool = (
346→    name: string,
347→    description: string,
348→    targetSchema: Record<string, z.ZodTypeAny>,
349→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
350→  ) => {
351→    server.tool(name, description, { ...targetSchema, ...typeSchema }, async (params) => {
352→      const validationError = validateTypeParams(params as TypeParams);
353→      if (validationError) return validationError;
354→
355→      const target = resolveTarget(params as AnyTargetParams);
356→      if (!Array.isArray(target)) return target;
357→
358→      const built = buildTypeArgs(target, params as TypeParams);
359→      return await runNative(built.args, { stdinText: built.stdinText });
360→    });
361→  };
362→
363→  const registerSwipeTool = (
364→    name: string,
365→    description: string,
366→    targetSchema: Record<string, z.ZodTypeAny>,
367→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
368→  ) => {
369→    server.tool(name, description, { ...targetSchema, ...swipeSchema }, async (params) => {
370→      const target = resolveTarget(params as AnyTargetParams);
371→      if (!Array.isArray(target)) return target;
372→      return await runNative(buildSwipeArgs(target, params as SwipeParams));
373→    });
374→  };
375→
376→  const registerScrollTool = (
377→    name: string,
378→    description: string,
379→    targetSchema: Record<string, z.ZodTypeAny>,
380→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
381→  ) => {
382→    server.tool(name, description, { ...targetSchema, ...scrollSchema }, async (params) => {
383→      const target = resolveTarget(params as AnyTargetParams);
384→      if (!Array.isArray(target)) return target;
385→      return await runNative(buildScrollArgs(target, params as ScrollParams));
386→    });
387→  };
388→
389→  const registerDragDropTool = (
390→    name: string,
391→    description: string,
392→    targetSchema: Record<string, z.ZodTypeAny>,
393→    resolveTarget: (params: AnyTargetParams) => string[] | ToolTextResult
394→  ) => {
395→    server.tool(name, description, { ...targetSchema, ...dragDropSchema }, async (params) => {
396→      const target = resolveTarget(params as AnyTargetParams);
397→      if (!Array.isArray(target)) return target;
398→      return await runNative(buildDragDropArgs(target, params as DragDropParams));
399→    });
400→  };
401→
402→  // Explicit simulator/macOS split tools
403→  registerDescribeTool(
404→    "sim_describe_ui",
405→    "Describe Simulator app UI hierarchy (inside simulator app content by default).",
406→    simTargetSchema,
407→    resolveSimulatorTargetArgs
408→  );
409→  registerDescribeTool(
410→    "mac_describe_ui",
411→    "Describe macOS app UI hierarchy.",
412→    macTargetSchema,
413→    resolveMacTargetArgs
414→  );
415→
416→  registerSearchTool(
417→    "sim_search_ui",
418→    "Search Simulator app UI elements by text, identifier, or label.",
419→    simTargetSchema,
420→    resolveSimulatorTargetArgs
421→  );
422→  registerSearchTool(
423→    "mac_search_ui",
424→    "Search macOS app UI elements by text, identifier, or label.",
425→    macTargetSchema,
426→    resolveMacTargetArgs
427→  );
428→
429→  registerTapTool(
430→    "sim_tap",
431→    "Tap simulator coordinates or in-app accessibility element. Selector lookup defaults to in-app content; set all=true to include Simulator chrome UI.",
432→    simTargetSchema,
433→    tapSchema,
434→    resolveSimulatorTargetArgs
435→  );
436→  registerTapTool(
437→    "mac_tap",
438→    "Tap macOS app coordinates or accessibility element.",
439→    macTargetSchema,
440→    tapSchemaWithoutAll,
441→    resolveMacTargetArgs
442→  );
443→
444→  registerTypeTool("sim_type_text", "Type text in Simulator target.", simTargetSchema, resolveSimulatorTargetArgs);
445→  registerTypeTool("mac_type_text", "Type text in macOS app target.", macTargetSchema, resolveMacTargetArgs);
446→
447→  registerSwipeTool("sim_swipe", "Perform swipe gesture in Simulator target.", simTargetSchema, resolveSimulatorTargetArgs);
448→  registerSwipeTool("mac_swipe", "Perform swipe gesture in macOS app target.", macTargetSchema, resolveMacTargetArgs);
449→
450→  registerScrollTool("sim_scroll", "Send scroll wheel events to Simulator target.", simTargetSchema, resolveSimulatorTargetArgs);
451→  registerScrollTool("mac_scroll", "Send scroll wheel events to macOS app target.", macTargetSchema, resolveMacTargetArgs);
452→
453→  registerDragDropTool("sim_drag_drop", "Drag and drop in Simulator target.", simTargetSchema, resolveSimulatorTargetArgs);
454→  registerDragDropTool("mac_drag_drop", "Drag and drop in macOS app target.", macTargetSchema, resolveMacTargetArgs);
455→}
