1135→test("BAEPSAE_NATIVE_PATH override uses specified binary", async () => {
1136→  // Create a fake executable to test the env override
1137→  const tmpDir = path.join(os.tmpdir(), `baepsae-unit-test-${Date.now()}`);
1138→  mkdirSync(tmpDir, { recursive: true });
1139→
1140→  const fakeBinary = path.join(tmpDir, "fake-baepsae-native");
1141→  // Write a simple script that echoes a known string
1142→  writeFileSync(fakeBinary, '#!/bin/sh\necho "FAKE_NATIVE_BINARY"\n');
1143→  chmodSync(fakeBinary, 0o755);
1144→
1145→  try {
1146→    await withClient(async (client) => {
1147→      // list_apps calls runNative which calls resolveNativeBinary
1148→      const result = await client.callTool({
1149→        name: "list_apps",
1150→        arguments: {},
1151→      });
1152→      const text = extractText(result);
1153→      // The executable path should point to our fake binary
1154→      assert.match(text, /fake-baepsae-native/, "Should use the overridden binary path");
1155→      assert.match(text, /FAKE_NATIVE_BINARY/, "Should contain output from fake binary");
1156→    }, { BAEPSAE_NATIVE_PATH: fakeBinary });
1157→  } finally {
1158→    rmSync(tmpDir, { recursive: true, force: true });
1159→  }
1160→});
