 643→    name: "terminate_app",
 644→    arguments: { udid, bundleId: "com.baepsae.sampleapp" },
 645→  });
 646→  await sleep(500);
 647→  // Ensure the app is installed (may have been uninstalled by a previous test run)
 648→  const appPath = findSampleApp();
 649→  if (appPath) {
 650→    await client.callTool({
 651→      name: "install_app",
 652→      arguments: { udid, path: appPath },
 653→    });
 654→  }
 655→  await client.callTool({
 656→    name: "launch_app",
 657→    arguments: { udid, bundleId: "com.baepsae.sampleapp" },
 658→  });
 659→  // Wait until the app UI shows "Ready" (up to 10s)
 660→  await waitForUI(client, udid, "test-label", (text) => text.includes("Ready"), 10000);
 661→}
