import type { McpServer } from "@modelcontextprotocol/sdk/server/mcp.js";
import { z } from "zod";

import type { ToolTextResult } from "../types.js";
import {
  unifiedTargetSchema,
  resolveUnifiedTargetArgs,
  pushOption,
  runNative,
} from "../utils.js";

type UnifiedTargetParams = {
  udid?: string;
  bundleId?: string;
  appName?: string;
};

type DescribeParams = {
  output?: string;
  focusId?: string;
  all?: boolean;
  offset?: number;
  limit?: number;
  rootElementId?: string;
  role?: string;
  visibleOnly?: boolean;
  maxDepth?: number;
  summary?: boolean;
};

type SearchParams = {
  query: string;
