  return args;
}

export function registerUITools(server: McpServer): void {
  server.tool(
    "analyze_ui",
    "Describe app UI hierarchy. Works with both simulator (udid) and macOS (bundleId/appName) targets.",
    { ...unifiedTargetSchema, ...describeSchema },
