  // Write a simple script that echoes a known string
  writeFileSync(fakeBinary, '#!/bin/sh\necho "FAKE_NATIVE_BINARY"\n');
  chmodSync(fakeBinary, 0o755);

  try {
    await withClient(async (client) => {
