182→function buildDescribeArgs(target: string[], params: DescribeParams): string[] {
183→  const args = ["describe-ui", ...target];
184→  pushOption(args, "--output", params.output);
185→  pushOption(args, "--focus-id", params.focusId);
186→  if (params.all) {
187→    args.push("--all");
188→  }
189→  pushOption(args, "--offset", params.offset);
190→  pushOption(args, "--limit", params.limit);
191→  pushOption(args, "--root-element-id", params.rootElementId);
192→  pushOption(args, "--role", params.role);
193→  if (params.visibleOnly) {
194→    args.push("--visible-only");
195→  }
196→  pushOption(args, "--max-depth", params.maxDepth);
197→  if (params.summary) {
198→    args.push("--summary");
199→  }
200→  return args;
201→}
