203→function buildSearchArgs(target: string[], params: SearchParams): string[] {
204→  const args = ["search-ui", ...target, "--query", params.query];
205→  pushOption(args, "--role", params.role);
206→  if (params.visibleOnly) {
207→    args.push("--visible-only");
208→  }
209→  pushOption(args, "--max-depth", params.maxDepth);
210→  return args;
211→}
