126→export function resolveUnifiedTargetArgs(params: { udid?: string; bundleId?: string; appName?: string }): string[] | ToolTextResult {
127→  const modes = [params.udid, params.bundleId, params.appName].filter(Boolean).length;
128→  if (modes === 0) {
129→    return {
130→      content: [{ type: "text", text: "Provide exactly one target: udid, bundleId, or appName." }],
131→      isError: true,
132→    };
133→  }
134→  if (modes > 1) {
135→    return {
136→      content: [{ type: "text", text: "Provide exactly one target: udid, bundleId, or appName." }],
137→      isError: true,
138→    };
139→  }
140→  if (params.udid) return ["--udid", params.udid];
141→  if (params.bundleId) return ["--bundle-id", params.bundleId];
142→  return ["--app-name", params.appName!];
143→}
