145→export function resolveSimulatorTargetArgs(params: { udid?: string }): string[] | ToolTextResult {
146→  if (!params.udid) {
147→    return {
148→      content: [{ type: "text", text: "Provide udid." }],
149→      isError: true,
150→    };
151→  }
152→  return ["--udid", params.udid];
153→}
154→
155→export function resolveMacTargetArgs(params: { bundleId?: string; appName?: string }): string[] | ToolTextResult {
156→  const modes = [params.bundleId, params.appName].filter(Boolean).length;
157→  if (modes !== 1) {
158→    return {
159→      content: [{ type: "text", text: "Provide exactly one of bundleId or appName." }],
160→      isError: true,
161→    };
162→  }
163→  if (params.bundleId) {
164→    return ["--bundle-id", params.bundleId];
165→  }
166→  return ["--app-name", params.appName!];
167→}
