142→function validateTapParams(params: TapParams): ToolTextResult | null {
143→  const hasX = params.x !== undefined;
144→  const hasY = params.y !== undefined;
145→  const hasSelector = params.id !== undefined || params.label !== undefined;
146→
147→  if (hasX !== hasY) {
148→    return {
149→      content: [{ type: "text", text: "Both x and y must be provided together." }],
150→      isError: true,
151→    };
152→  }
153→
154→  if (hasX && hasSelector) {
155→    return {
156→      content: [{ type: "text", text: "Provide either x/y coordinates or id/label, not both." }],
157→      isError: true,
158→    };
159→  }
160→
161→  if (!hasX && !params.id && !params.label) {
162→    return {
163→      content: [{ type: "text", text: "Provide either x/y coordinates, id, or label." }],
164→      isError: true,
165→    };
166→  }
167→
168→  return null;
169→}
170→
171→function validateTypeParams(params: TypeParams): ToolTextResult | null {
172→  const modes = [params.text !== undefined, params.stdinText !== undefined, params.file !== undefined].filter(Boolean).length;
173→  if (modes !== 1) {
174→    return {
175→      content: [{ type: "text", text: "Provide exactly one of text, stdinText, or file." }],
176→      isError: true,
177→    };
178→  }
179→  return null;
180→}
