213→function buildTapArgs(target: string[], params: TapParams): string[] {
214→  const args = ["tap"];
215→  pushOption(args, "-x", params.x);
216→  pushOption(args, "-y", params.y);
217→  pushOption(args, "--id", params.id);
218→  pushOption(args, "--label", params.label);
219→  if (params.all) {
220→    args.push("--all");
221→  }
222→  if (params.double) {
223→    args.push("--double");
224→  }
225→  pushOption(args, "--pre-delay", params.preDelay);
226→  pushOption(args, "--post-delay", params.postDelay);
227→  args.push(...target);
228→  return args;
229→}
