231→function buildTypeArgs(target: string[], params: TypeParams): { args: string[]; stdinText?: string } {
232→  const args = ["type"];
233→  if (params.text !== undefined) {
234→    args.push(params.text);
235→  }
236→  if (params.stdinText !== undefined) {
237→    args.push("--stdin");
238→  }
239→  if (params.file !== undefined) {
240→    args.push("--file", params.file);
241→  }
242→  args.push(...target);
243→  return { args, stdinText: params.stdinText };
244→}
