282→export function registerUITools(server: McpServer): void {
283→  server.tool(
284→    "analyze_ui",
285→    "Describe app UI hierarchy. Works with both simulator (udid) and macOS (bundleId/appName) targets.",
286→    { ...unifiedTargetSchema, ...describeSchema },
287→    async (params) => {
288→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
289→      if (!Array.isArray(target)) return target;
290→      return await runNative(buildDescribeArgs(target, params as DescribeParams));
291→    }
292→  );
293→
294→  server.tool(
295→    "query_ui",
296→    "Search app UI elements by text, identifier, or label. Works with both simulator and macOS targets.",
297→    { ...unifiedTargetSchema, ...searchSchema },
298→    async (params) => {
299→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
300→      if (!Array.isArray(target)) return target;
301→      return await runNative(buildSearchArgs(target, params as SearchParams));
302→    }
303→  );
304→
305→  server.tool(
306→    "tap",
307→    "Tap coordinates or accessibility element. Selector lookup defaults to in-app content; set all=true to include Simulator chrome UI.",
308→    { ...unifiedTargetSchema, ...tapSchema },
309→    async (params) => {
310→      const validationError = validateTapParams(params as TapParams);
311→      if (validationError) return validationError;
312→
313→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
314→      if (!Array.isArray(target)) return target;
315→      return await runNative(buildTapArgs(target, params as TapParams));
316→    }
317→  );
318→
319→  server.tool(
320→    "type_text",
321→    "Type text into the target app.",
322→    { ...unifiedTargetSchema, ...typeSchema },
323→    async (params) => {
324→      const validationError = validateTypeParams(params as TypeParams);
325→      if (validationError) return validationError;
326→
327→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
328→      if (!Array.isArray(target)) return target;
329→
330→      const built = buildTypeArgs(target, params as TypeParams);
331→      return await runNative(built.args, { stdinText: built.stdinText });
332→    }
333→  );
334→
335→  server.tool(
336→    "swipe",
337→    "Perform swipe gesture in the target app.",
338→    { ...unifiedTargetSchema, ...swipeSchema },
339→    async (params) => {
340→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
341→      if (!Array.isArray(target)) return target;
342→      return await runNative(buildSwipeArgs(target, params as SwipeParams));
343→    }
344→  );
345→
346→  server.tool(
347→    "scroll",
348→    "Send scroll wheel events to the target app.",
349→    { ...unifiedTargetSchema, ...scrollSchema },
350→    async (params) => {
351→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
352→      if (!Array.isArray(target)) return target;
353→      return await runNative(buildScrollArgs(target, params as ScrollParams));
354→    }
355→  );
356→
357→  server.tool(
358→    "drag_drop",
359→    "Drag and drop in the target app.",
360→    { ...unifiedTargetSchema, ...dragDropSchema },
361→    async (params) => {
362→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
363→      if (!Array.isArray(target)) return target;
364→      return await runNative(buildDragDropArgs(target, params as DragDropParams));
365→    }
366→  );
367→}
