142→function validateTapParams(params: TapParams): ToolTextResult | null {
143→  const hasX = params.x !== undefined;
144→  const hasY = params.y !== undefined;
145→  const hasSelector = params.id !== undefined || params.label !== undefined;
146→
147→  if (hasX !== hasY) {
148→    return {
149→      content: [{ type: "text", text: "Both x and y must be provided together." }],
150→      isError: true,
151→    };
152→  }
153→
154→  if (hasX && hasSelector) {
155→    return {
156→      content: [{ type: "text", text: "Provide either x/y coordinates or id/label, not both." }],
157→      isError: true,
158→    };
159→  }
160→
161→  if (!hasX && !params.id && !params.label) {
162→    return {
163→      content: [{ type: "text", text: "Provide either x/y coordinates, id, or label." }],
164→      isError: true,
165→    };
166→  }
167→
168→  return null;
169→}
170→
171→function validateTypeParams(params: TypeParams): ToolTextResult | null {
172→  const modes = [params.text !== undefined, params.stdinText !== undefined, params.file !== undefined].filter(Boolean).length;
173→  if (modes !== 1) {
174→    return {
175→      content: [{ type: "text", text: "Provide exactly one of text, stdinText, or file." }],
176→      isError: true,
177→    };
178→  }
179→  return null;
180→}
181→
182→function buildDescribeArgs(target: string[], params: DescribeParams): string[] {
183→  const args = ["describe-ui", ...target];
184→  pushOption(args, "--output", params.output);
185→  pushOption(args, "--focus-id", params.focusId);
186→  if (params.all) {
187→    args.push("--all");
188→  }
189→  pushOption(args, "--offset", params.offset);
190→  pushOption(args, "--limit", params.limit);
191→  pushOption(args, "--root-element-id", params.rootElementId);
192→  pushOption(args, "--role", params.role);
193→  if (params.visibleOnly) {
194→    args.push("--visible-only");
195→  }
196→  pushOption(args, "--max-depth", params.maxDepth);
197→  if (params.summary) {
198→    args.push("--summary");
199→  }
200→  return args;
201→}
202→
203→function buildSearchArgs(target: string[], params: SearchParams): string[] {
204→  const args = ["search-ui", ...target, "--query", params.query];
205→  pushOption(args, "--role", params.role);
206→  if (params.visibleOnly) {
207→    args.push("--visible-only");
208→  }
209→  pushOption(args, "--max-depth", params.maxDepth);
210→  return args;
211→}
212→
213→function buildTapArgs(target: string[], params: TapParams): string[] {
214→  const args = ["tap"];
215→  pushOption(args, "-x", params.x);
216→  pushOption(args, "-y", params.y);
217→  pushOption(args, "--id", params.id);
218→  pushOption(args, "--label", params.label);
219→  if (params.all) {
220→    args.push("--all");
221→  }
222→  if (params.double) {
223→    args.push("--double");
224→  }
225→  pushOption(args, "--pre-delay", params.preDelay);
226→  pushOption(args, "--post-delay", params.postDelay);
227→  args.push(...target);
228→  return args;
229→}
230→
231→function buildTypeArgs(target: string[], params: TypeParams): { args: string[]; stdinText?: string } {
232→  const args = ["type"];
233→  if (params.text !== undefined) {
234→    args.push(params.text);
235→  }
236→  if (params.stdinText !== undefined) {
237→    args.push("--stdin");
238→  }
239→  if (params.file !== undefined) {
240→    args.push("--file", params.file);
241→  }
242→  args.push(...target);
243→  return { args, stdinText: params.stdinText };
244→}
245→
246→function buildSwipeArgs(target: string[], params: SwipeParams): string[] {
247→  const args = [
248→    "swipe",
249→    "--start-x",
250→    String(params.startX),
251→    "--start-y",
252→    String(params.startY),
253→    "--end-x",
254→    String(params.endX),
255→    "--end-y",
256→    String(params.endY),
257→  ];
258→  pushOption(args, "--duration", params.duration);
259→  pushOption(args, "--pre-delay", params.preDelay);
260→  pushOption(args, "--post-delay", params.postDelay);
261→  args.push(...target);
262→  return args;
263→}
264→
265→function buildScrollArgs(target: string[], params: ScrollParams): string[] {
266→  const args = ["scroll", ...target];
267→  pushOption(args, "--delta-x", params.deltaX);
268→  pushOption(args, "--delta-y", params.deltaY);
269→  pushOption(args, "-x", params.x);
270→  pushOption(args, "-y", params.y);
271→  return args;
272→}
273→
274→function buildDragDropArgs(target: string[], params: DragDropParams): string[] {
275→  const args = ["drag-drop", ...target];
276→  args.push("--start-x", String(params.startX), "--start-y", String(params.startY));
277→  args.push("--end-x", String(params.endX), "--end-y", String(params.endY));
278→  pushOption(args, "--duration", params.duration);
279→  return args;
280→}
