171→      if (!Array.isArray(target)) return target;
172→      return await runNative(buildTouchArgs(target, params as TouchParams));
173→    }
174→  );
175→
176→  server.tool(
177→    "gesture",
178→    "Execute a preset gesture pattern.",
179→    {
180→      udid: z.string().min(1).describe("Simulator UDID"),
181→      preset: z.enum(GESTURE_PRESETS).describe("Gesture preset"),
182→      screenWidth: z.number().optional().describe("Screen width in points"),
183→      screenHeight: z.number().optional().describe("Screen height in points"),
184→      duration: z.number().optional().describe("Gesture duration in seconds"),
185→      preDelay: z.number().optional().describe("Delay before gesture in seconds"),
186→      postDelay: z.number().optional().describe("Delay after gesture in seconds"),
187→    },
188→    async (params) => {
189→      const args = ["gesture", params.preset];
190→      pushOption(args, "--screen-width", params.screenWidth);
191→      pushOption(args, "--screen-height", params.screenHeight);
192→      pushOption(args, "--duration", params.duration);
193→      pushOption(args, "--pre-delay", params.preDelay);
194→      pushOption(args, "--post-delay", params.postDelay);
195→      args.push("--udid", params.udid);
196→      return await runNative(args);
197→    }
198→  );
199→}
