 15→type UnifiedTargetParams = {
 16→  udid?: string;
 17→  bundleId?: string;
 18→  appName?: string;
 19→};
 20→
 21→type KeyParams = {
 22→  keycode: number;
 23→  duration?: number;
 24→};
 25→
 26→type KeySequenceParams = {
 27→  keycodes: number[] | string;
 28→  delay?: number;
 29→};
 30→
 31→type KeyComboParams = {
 32→  modifiers: number[];
 33→  key: number;
 34→};
 35→
 36→type TouchParams = {
 37→  x: number;
 38→  y: number;
 39→  down?: boolean;
 40→  up?: boolean;
 41→  delay?: number;
 42→};
 43→
 44→const keySchema = {
 45→  keycode: keycodeSchema.describe("HID keycode (0-255)"),
 46→  duration: z.number().optional().describe("Hold duration in seconds"),
 47→};
 48→
 49→const keySequenceSchema = {
 50→  keycodes: z
 51→    .union([
 52→      z.array(keycodeSchema).min(1).describe("Array of HID keycodes"),
 53→      z.string().min(1).describe("Comma-separated HID keycodes"),
 54→    ])
 55→    .describe("Key sequence"),
 56→  delay: z.number().optional().describe("Delay between key presses in seconds"),
 57→};
 58→
 59→const keyComboSchema = {
 60→  modifiers: z.array(keycodeSchema).min(1).describe("Modifier keycodes"),
 61→  key: keycodeSchema.describe("Keycode to press while modifiers are held"),
 62→};
 63→
 64→const touchSchema = {
 65→  x: z.number().describe("X coordinate"),
 66→  y: z.number().describe("Y coordinate"),
 67→  down: z.boolean().optional().describe("Send touch down event"),
 68→  up: z.boolean().optional().describe("Send touch up event"),
 69→  delay: z.number().optional().describe("Delay between down/up in seconds"),
 70→};
 71→
 72→function buildKeyArgs(target: string[], params: KeyParams): string[] {
 73→  const args = ["key", String(params.keycode)];
 74→  pushOption(args, "--duration", params.duration);
 75→  args.push(...target);
 76→  return args;
 77→}
 78→
 79→function buildKeySequenceArgs(target: string[], params: KeySequenceParams): string[] {
 80→  const keycodes = Array.isArray(params.keycodes) ? params.keycodes.join(",") : params.keycodes;
 81→  const args = ["key-sequence", "--keycodes", keycodes];
 82→  pushOption(args, "--delay", params.delay);
 83→  args.push(...target);
 84→  return args;
 85→}
 86→
 87→function buildKeyComboArgs(target: string[], params: KeyComboParams): string[] {
 88→  return [
 89→    "key-combo",
 90→    "--modifiers",
 91→    params.modifiers.join(","),
 92→    "--key",
 93→    String(params.key),
 94→    ...target,
 95→  ];
 96→}
 97→
 98→function buildTouchArgs(target: string[], params: TouchParams): string[] {
 99→  const args = ["touch", "-x", String(params.x), "-y", String(params.y)];
100→  if (params.down === undefined && params.up === undefined) {
101→    args.push("--down", "--up");
102→  } else {
103→    if (params.down) {
104→      args.push("--down");
105→    }
106→    if (params.up) {
107→      args.push("--up");
108→    }
109→  }
110→  pushOption(args, "--delay", params.delay);
111→  args.push(...target);
112→  return args;
113→}
