180→      udid: z.string().min(1).describe("Simulator UDID"),
181→      preset: z.enum(GESTURE_PRESETS).describe("Gesture preset"),
182→      screenWidth: z.number().optional().describe("Screen width in points"),
183→      screenHeight: z.number().optional().describe("Screen height in points"),
184→      duration: z.number().optional().describe("Gesture duration in seconds"),
185→      preDelay: z.number().optional().describe("Delay before gesture in seconds"),
