192→      pushOption(args, "--duration", params.duration);
193→      pushOption(args, "--pre-delay", params.preDelay);
194→      pushOption(args, "--post-delay", params.postDelay);
195→      args.push("--udid", params.udid);
196→      return await runNative(args);
197→    }
