 96→    async (params) => {
 97→      const target = resolveUnifiedTargetArgs(params as UnifiedTargetParams);
 98→      if (!Array.isArray(target)) return target;
 99→      return await runNative(buildRightClickArgs(target, params as RightClickParams));
100→    }
101→  );
