103→  server.tool(
104→    "menu_action",
105→    "Execute a menu bar action in a macOS app.",
106→    {
107→      bundleId: z.string().optional(),
108→      appName: z.string().optional(),
109→      menu: z.string().describe("Menu name (e.g. 'File')"),
110→      item: z.string().describe("Menu item name (e.g. 'Save')"),
111→    },
112→    async (params) => {
113→      const args = ["menu-action"];
114→      if (params.bundleId) args.push("--bundle-id", params.bundleId);
115→      else if (params.appName) args.push("--app-name", params.appName);
116→      else return { content: [{ type: "text", text: "Provide bundleId or appName." }], isError: true };
117→      args.push("--menu", params.menu, "--item", params.item);
118→      return await runNative(args);
119→    }
120→  );
121→
122→  server.tool(
123→    "clipboard",
124→    "Read or write the system clipboard.",
125→    {
126→      action: z.enum(["read", "write"]).describe("Read or write clipboard"),
127→      text: z.string().optional().describe("Text to write (required for write action)"),
128→    },
129→    async (params) => {
130→      const args = ["clipboard"];
131→      if (params.action === "read") {
132→        args.push("--read");
133→      } else {
134→        if (!params.text) return { content: [{ type: "text", text: "text is required for write action." }], isError: true };
135→        args.push("--write", params.text);
136→      }
137→      return await runNative(args);
138→    }
139→  );
140→}
