  1→import type { McpServer } from "@modelcontextprotocol/sdk/server/mcp.js";
  2→import { z } from "zod";
  3→
  4→import {
  5→  SERVER_NAME,
  6→  SERVER_VERSION,
  7→  NATIVE_BINARY_ENV,
  8→  BAEPSAE_SUBCOMMANDS,
  9→  resolveNativeBinary,
 10→  executeCommand,
 11→  runNative,
 12→} from "../utils.js";
 13→
 14→export function registerInfoTools(server: McpServer): void {
 15→  server.tool(
 16→    "baepsae_help",
 17→    "Show help. Optionally pass subcommand name for compatibility reference.",
 18→    {
 19→      subcommand: z
 20→        .enum(BAEPSAE_SUBCOMMANDS)
 21→        .optional()
 22→        .describe("Optional legacy subcommand name for compatibility reference"),
 23→    },
 24→    async (params) => {
 25→      const lines = [
 26→        `Server: ${SERVER_NAME} v${SERVER_VERSION}`,
 27→        "Mode: native-layer + simctl",
 28→        "",
 29→        "supported tools:",
 30→        "",
 31→        "UI (unified — pass udid for simulator, bundleId/appName for macOS):",
 32→        "- analyze_ui, query_ui, tap, type_text, swipe, scroll, drag_drop",
 33→        "",
 34→        "Input (unified):",
 35→        "- key, key_sequence, key_combo, touch",
 36→        "",
 37→        "System (unified):",
 38→        "- list_windows, activate_app, screenshot_app, right_click",
 39→        "",
 40→        "Simulator-only:",
 41→        "- list_simulators, screenshot, record_video, stream_video, open_url, install_app, launch_app, terminate_app, uninstall_app, button, gesture",
 42→        "",
 43→        "macOS/system:",
 44→        "- list_apps, menu_action, get_focused_app, clipboard, baepsae_help, baepsae_version",
 45→        "",
 46→        "Accessibility tip:",
 47→        "- launch_app -> analyze_ui/query_ui -> tap(id/label)",
 48→        "- tap/right_click selector lookup defaults to in-app content (set all=true to include Simulator chrome UI)",
 49→        "",
 50→        `Native binary requirement: build native binary or set ${NATIVE_BINARY_ENV}`,
 51→      ];
 52→
 53→      if (params.subcommand) {
 54→        lines.push("", `Requested legacy subcommand: ${params.subcommand}`);
 55→      }
 56→
 57→      return {
 58→        content: [{ type: "text", text: lines.join("\n") }],
 59→        isError: false,
 60→      };
 61→    }
 62→  );
 63→
 64→  server.tool("baepsae_version", "Show server and native binary versions.", {}, async () => {
 65→    const lines = [
 66→      `${SERVER_NAME} ${SERVER_VERSION}`,
 67→      `Node.js: ${process.version}`,
 68→      `Platform: ${process.platform} ${process.arch}`,
 69→    ];
 70→
 71→    try {
 72→      const binary = resolveNativeBinary();
 73→      const result = await executeCommand(binary, ["--version"]);
 74→      lines.push(`Native: ${result.stdout}`);
 75→    } catch {
 76→      lines.push("Native: not built (run npm run build)");
 77→    }
 78→
 79→    return {
 80→      content: [{ type: "text", text: lines.join("\n") }],
 81→      isError: false,
 82→    };
 83→  });
 84→
 85→  server.tool(
 86→    "list_apps",
 87→    "List running macOS applications with their bundle IDs.",
 88→    {},
 89→    async () => {
 90→      return await runNative(["list-apps"]);
 91→    }
 92→  );
 93→
 94→  server.tool(
 95→    "get_focused_app",
 96→    "Get information about the currently focused macOS app.",
 97→    {},
 98→    async () => {
 99→      return await runNative(["get-focused-app"]);
100→    }
101→  );
102→}
