 1→import type { McpServer } from "@modelcontextprotocol/sdk/server/mcp.js";
 2→import { z } from "zod";
 3→
 4→import { pushOption, ensureOutputPath, runNative, runSimctl } from "../utils.js";
 5→
 6→export function registerMediaTools(server: McpServer): void {
 7→  server.tool(
 8→    "stream_video",
 9→    "Stream simulator frames.",
10→    {
11→      udid: z.string().min(1).describe("Simulator UDID"),
12→      output: z.string().optional().describe("Destination output path"),
13→      durationSeconds: z.number().positive().optional().describe("Capture duration in seconds"),
14→    },
15→    async (params) => {
16→      const args = ["stream-video", "--udid", params.udid];
17→
18→      const durationSeconds = params.durationSeconds ?? 10;
19→      const outputPath = params.output ?? `simulator-stream-${Date.now()}.mov`;
20→      const resolvedOutput = await ensureOutputPath(outputPath);
21→      pushOption(args, "--duration", durationSeconds);
22→      args.push("--output", resolvedOutput);
23→
24→      return await runNative(
25→        args,
26→        {
27→          timeoutMs: Math.max(15_000, Math.round((durationSeconds + 15) * 1000)),
28→        },
29→        {
30→          extraLines: [`Capture duration: ${durationSeconds}s`, `Output file: ${resolvedOutput}`],
31→        }
32→      );
33→    }
34→  );
35→
36→  server.tool(
37→    "record_video",
38→    "Record simulator display.",
39→    {
40→      udid: z.string().min(1).describe("Simulator UDID"),
41→      output: z.string().optional().describe("Output MOV file path"),
42→      durationSeconds: z.number().positive().optional().describe("Recording duration in seconds (default: 10)"),
43→    },
44→    async (params) => {
45→      const durationSeconds = params.durationSeconds ?? 10;
46→      const outputPath = params.output ?? `simulator-recording-${Date.now()}.mov`;
47→      const resolvedOutput = await ensureOutputPath(outputPath);
48→
49→      const extraLines = [
50→        `Recording duration: ${durationSeconds}s`,
51→        `Output file: ${resolvedOutput}`,
52→      ];
53→
54→      return await runSimctl(
55→        ["io", params.udid, "recordVideo", "--force", resolvedOutput],
56→        {
57→          timeoutMs: Math.max(15_000, Math.round((durationSeconds + 5) * 1000)),
58→        },
59→        {
60→          timeoutIsExpected: true,
61→          extraLines,
62→        }
63→      );
64→    }
65→  );
66→
67→  server.tool(
68→    "screenshot",
69→    "Capture a screenshot from simulator display using simctl screenshot.",
70→    {
71→      udid: z.string().min(1).describe("Simulator UDID"),
72→      output: z.string().optional().describe("Output PNG file path"),
73→    },
74→    async (params) => {
75→      const outputPath = params.output ?? `simulator-screenshot-${Date.now()}.png`;
76→      const resolvedOutput = await ensureOutputPath(outputPath);
77→
78→      return await runSimctl(["io", params.udid, "screenshot", resolvedOutput], undefined, {
79→        extraLines: [`Output file: ${resolvedOutput}`],
80→      });
81→    }
82→  );
83→}
