 5→import { runSimctl } from "../utils.js";
 6→
 7→export function registerSimulatorTools(server: McpServer): void {
 8→  server.tool("list_simulators", "List available simulators using simctl.", {}, async () => {
 9→    return await runSimctl(["list", "devices", "available"]);
10→  });
11→
12→  server.tool(
13→    "open_url",
14→    "Open a URL in the simulator (e.g. Safari or deep link).",
15→    {
16→      udid: z.string().min(1).describe("Simulator UDID"),
17→      url: z.string().min(1).describe("URL to open"),
18→    },
19→    async (params) => {
20→      return await runSimctl(["openurl", params.udid, params.url]);
21→    }
22→  );
23→
24→  server.tool(
25→    "install_app",
26→    "Install an app (.app, .ipa) on the simulator.",
27→    {
28→      udid: z.string().min(1).describe("Simulator UDID"),
29→      path: z.string().min(1).describe("Path to .app or .ipa file"),
30→    },
31→    async (params) => {
32→      const resolvedPath = resolve(params.path);
33→      return await runSimctl(["install", params.udid, resolvedPath]);
34→    }
35→  );
36→
37→  server.tool(
38→    "uninstall_app",
39→    "Uninstall an app from the simulator.",
40→    {
41→      udid: z.string().min(1).describe("Simulator UDID"),
42→      bundleId: z.string().min(1).describe("App Bundle Identifier (e.g. com.example.app)"),
43→    },
44→    async (params) => {
45→      return await runSimctl(["uninstall", params.udid, params.bundleId]);
46→    }
47→  );
48→
49→  server.tool(
50→    "launch_app",
51→    "Launch an installed app on the simulator.",
52→    {
53→      udid: z.string().min(1).describe("Simulator UDID"),
54→      bundleId: z.string().min(1).describe("App Bundle Identifier"),
55→      args: z.array(z.string()).optional().describe("Arguments to pass to the app"),
56→      env: z.record(z.string()).optional().describe("Environment variables"),
57→    },
58→    async (params) => {
59→      const args = ["launch", params.udid, params.bundleId];
60→      if (params.args) {
61→        args.push(...params.args);
62→      }
63→      const env: Record<string, string> | undefined = params.env
64→        ? Object.fromEntries(Object.entries(params.env).map(([k, v]) => [`SIMCTL_CHILD_${k}`, v]))
65→        : undefined;
66→      return await runSimctl(args, { env });
67→    }
68→  );
69→
70→  server.tool(
71→    "terminate_app",
72→    "Terminate a running app on the simulator.",
73→    {
74→      udid: z.string().min(1).describe("Simulator UDID"),
75→      bundleId: z.string().min(1).describe("App Bundle Identifier"),
