1102→test("BAEPSAE_NATIVE_PATH override uses specified binary", async () => {
1103→  // Create a fake executable to test the env override
1104→  const tmpDir = path.join(os.tmpdir(), `baepsae-unit-test-${Date.now()}`);
1105→  mkdirSync(tmpDir, { recursive: true });
1106→
1107→  const fakeBinary = path.join(tmpDir, "fake-baepsae-native");
1108→  // Write a simple script that echoes a known string
1109→  writeFileSync(fakeBinary, '#!/bin/sh\necho "FAKE_NATIVE_BINARY"\n');
1110→  chmodSync(fakeBinary, 0o755);
1111→
1112→  try {
1113→    await withClient(async (client) => {
1114→      // list_apps calls runNative which calls resolveNativeBinary
1115→      const result = await client.callTool({
1116→        name: "list_apps",
1117→        arguments: {},
1118→      });
1119→      const text = extractText(result);
1120→      // The executable path should point to our fake binary
1121→      assert.match(text, /fake-baepsae-native/, "Should use the overridden binary path");
1122→      assert.match(text, /FAKE_NATIVE_BINARY/, "Should contain output from fake binary");
1123→    }, { BAEPSAE_NATIVE_PATH: fakeBinary });
1124→  } finally {
1125→    rmSync(tmpDir, { recursive: true, force: true });
1126→  }
1127→});
