 1→#!/usr/bin/env node
 2→
 3→import type { McpServer as McpServerType } from "@modelcontextprotocol/sdk/server/mcp.js";
 4→
 5→import { PACKAGE_NAME, PACKAGE_VERSION } from "./version.js";
 6→
 7→const VERSION_FLAG_ARGS = new Set(["--version", "-v"]);
 8→
 9→function hasVersionFlag(argv: string[]): boolean {
10→  return argv.some((arg) => VERSION_FLAG_ARGS.has(arg));
11→}
12→
13→// Fast path: avoid loading MCP SDK/tool modules when only version output is needed.
14→if (hasVersionFlag(process.argv)) {
15→  console.log(`${PACKAGE_NAME} ${PACKAGE_VERSION}`);
16→  process.exit(0);
17→}
18→
19→async function main(): Promise<void> {
20→  const [{ McpServer }, { StdioServerTransport }, { SERVER_NAME, SERVER_VERSION }, { registerInfoTools }, { registerSimulatorTools }, { registerUITools }, { registerInputTools }, { registerMediaTools }, { registerSystemTools }] =
21→    await Promise.all([
22→      import("@modelcontextprotocol/sdk/server/mcp.js"),
23→      import("@modelcontextprotocol/sdk/server/stdio.js"),
24→      import("./utils.js"),
25→      import("./tools/info.js"),
26→      import("./tools/simulator.js"),
27→      import("./tools/ui.js"),
28→      import("./tools/input.js"),
29→      import("./tools/media.js"),
30→      import("./tools/system.js"),
31→    ]);
32→
33→  const server = new McpServer({
34→    name: SERVER_NAME,
35→    version: SERVER_VERSION,
36→  }) as unknown as McpServerType;
37→
38→  registerInfoTools(server);
39→  registerSimulatorTools(server);
40→  registerUITools(server);
41→  registerInputTools(server);
42→  registerMediaTools(server);
43→  registerSystemTools(server);
44→
45→  const transport = new StdioServerTransport();
46→  await server.connect(transport);
47→}
48→
49→main().catch((error) => {
50→  console.error("Failed to start mcp-baepsae server:", error);
51→  process.exit(1);
52→});
