   1→/**
   2→ * Unit tests for mcp-baepsae internal functions.
   3→ *
   4→ * Since the functions are not exported (refactoring is tracked in issue #8),
   5→ * these tests exercise them indirectly through the MCP client interface.
   6→ * They focus on edge cases, error paths, and specific behaviors that the
   7→ * existing contract tests do not cover.
   8→ *
   9→ * No booted simulator is required.
  10→ */
  11→
  12→import test from "node:test";
  13→import assert from "node:assert/strict";
  14→import path from "node:path";
  15→import os from "node:os";
  16→import { fileURLToPath } from "node:url";
  17→import { writeFileSync, chmodSync, mkdirSync, rmSync } from "node:fs";
  18→import { createRequire } from "node:module";
  19→
  20→import { Client } from "@modelcontextprotocol/sdk/client/index.js";
  21→import { StdioClientTransport } from "@modelcontextprotocol/sdk/client/stdio.js";
  22→
  23→const __filename = fileURLToPath(import.meta.url);
  24→const __dirname = path.dirname(__filename);
  25→const projectRoot = path.resolve(__dirname, "..");
  26→const require = createRequire(import.meta.url);
  27→const { version } = require("../package.json");
  28→
  29→// ---------------------------------------------------------------------------
  30→// Helpers
  31→// ---------------------------------------------------------------------------
  32→
  33→function extractText(result) {
  34→  return result.content
  35→    .filter((item) => item.type === "text")
  36→    .map((item) => item.text)
  37→    .join("\n");
  38→}
  39→
  40→async function withClient(run, envOverrides = {}) {
  41→  const client = new Client({ name: "baepsae-unit-test", version: "1.0.0" });
  42→  const transport = new StdioClientTransport({
  43→    command: "node",
  44→    args: ["dist/index.js"],
  45→    cwd: projectRoot,
  46→    stderr: "pipe",
  47→    env: { ...process.env, ...envOverrides },
  48→  });
  49→
  50→  await client.connect(transport);
  51→  try {
  52→    return await run(client);
  53→  } finally {
  54→    await client.close();
  55→  }
  56→}
  57→
