 59→export function isExecutable(filePath: string): boolean {
 60→  try {
 61→    accessSync(filePath, constants.X_OK);
 62→    return true;
 63→  } catch {
 64→    return false;
 65→  }
 66→}
 67→
 68→export function resolveNativeBinary(): string {
 69→  const override = process.env[NATIVE_BINARY_ENV];
 70→  if (override) {
 71→    const resolved = resolve(override);
 72→    if (!isExecutable(resolved)) {
 73→      throw new Error(`Configured ${NATIVE_BINARY_ENV} is not executable: ${resolved}`);
 74→    }
 75→    return resolved;
 76→  }
 77→
 78→  const candidates = [
 79→    resolve(PACKAGE_ROOT, "native", ".build", "release", NATIVE_BINARY_NAME),
 80→    resolve(PACKAGE_ROOT, "native", ".build", "debug", NATIVE_BINARY_NAME),
 81→  ];
 82→
 83→  for (const candidate of candidates) {
 84→    if (existsSync(candidate) && isExecutable(candidate)) {
 85→      return candidate;
 86→    }
 87→  }
 88→
 89→  // Issue #18: Provide a clearer error message depending on the environment
 90→  const messages: string[] = ["Native binary not found."];
 91→
 92→  if (process.platform !== "darwin") {
 93→    messages.push(
 94→      `Native features require macOS. Current platform: ${process.platform}.`
 95→    );
 96→  } else {
 97→    // Check if Swift toolchain is available
 98→    let swiftAvailable = false;
 99→    try {
100→      execFileSync("swift", ["--version"], { stdio: "ignore" });
101→      swiftAvailable = true;
102→    } catch {
103→      // swift not found or failed
104→    }
105→
106→    if (!swiftAvailable) {
107→      messages.push(
108→        "Swift toolchain not found. Install Xcode or Xcode Command Line Tools (xcode-select --install) to enable native features."
109→      );
110→    } else {
111→      messages.push(
112→        `Build it with "npm run build:native" or set ${NATIVE_BINARY_ENV}.`
113→      );
114→    }
115→  }
116→
117→  throw new Error(messages.join(" "));
118→}
