189→export async function executeCommand(
190→  executable: string,
191→  args: string[],
192→  options: CommandExecutionOptions = {}
193→): Promise<CommandExecutionResult> {
194→  const startedAt = Date.now();
195→  const timeoutMs = Math.max(1, options.timeoutMs ?? DEFAULT_TIMEOUT_MS);
196→  const captureStdout = options.captureStdout ?? !options.stdoutFilePath;
197→
198→  const outputPath = options.stdoutFilePath ? await ensureOutputPath(options.stdoutFilePath) : undefined;
199→
200→  return await new Promise<CommandExecutionResult>((resolvePromise, rejectPromise) => {
201→    const child = spawn(executable, args, {
202→      stdio: ["pipe", "pipe", "pipe"],
203→      env: options.env ? { ...process.env, ...options.env } : process.env,
204→    });
205→
206→    const stdoutChunks: Buffer[] = [];
207→    const stderrChunks: Buffer[] = [];
208→    let timedOut = false;
209→    let closed = false;
210→    let escalationTimer: NodeJS.Timeout | undefined;
211→    let outputFinished: Promise<void> | undefined;
212→
213→    if (outputPath) {
214→      const outputStream = createWriteStream(outputPath);
215→      outputFinished = new Promise<void>((resolveOutput, rejectOutput) => {
216→        outputStream.on("finish", () => resolveOutput());
217→        outputStream.on("error", (error) => rejectOutput(error));
218→      });
219→      child.stdout.pipe(outputStream);
220→    } else if (captureStdout) {
221→      child.stdout.on("data", (chunk: Buffer | string) => {
222→        stdoutChunks.push(Buffer.isBuffer(chunk) ? chunk : Buffer.from(chunk));
223→      });
224→    }
225→
226→    child.stderr.on("data", (chunk: Buffer | string) => {
227→      stderrChunks.push(Buffer.isBuffer(chunk) ? chunk : Buffer.from(chunk));
228→    });
229→
230→    child.on("error", (error) => {
231→      rejectPromise(new Error(`Failed to execute command: ${error.message}`));
232→    });
233→
234→    if (options.stdinText !== undefined) {
235→      child.stdin.write(options.stdinText);
236→    }
237→    child.stdin.end();
238→
239→    const timer = setTimeout(() => {
240→      if (closed) {
241→        return;
242→      }
243→
244→      timedOut = true;
245→      child.kill("SIGINT");
246→
247→      escalationTimer = setTimeout(() => {
248→        if (!closed) {
249→          child.kill("SIGTERM");
250→        }
251→      }, 800);
252→    }, timeoutMs);
253→
254→    child.on("close", async (exitCode, signal) => {
255→      closed = true;
256→      clearTimeout(timer);
257→      if (escalationTimer) {
258→        clearTimeout(escalationTimer);
259→      }
260→
261→      try {
262→        if (outputFinished) {
263→          await outputFinished;
264→        }
265→
266→        resolvePromise({
267→          executablePath: executable,
268→          args,
269→          stdout: captureStdout ? Buffer.concat(stdoutChunks).toString("utf8").trimEnd() : "",
270→          stderr: Buffer.concat(stderrChunks).toString("utf8").trimEnd(),
271→          exitCode,
272→          signal,
273→          timedOut,
274→          durationMs: Date.now() - startedAt,
275→        });
276→      } catch (error) {
277→        rejectPromise(error instanceof Error ? error : new Error(String(error)));
278→      }
279→    });
280→  });
281→}
282→
283→function commandToString(executableName: string, args: string[]): string {
284→  return `${quoteArg(executableName)} ${args.map((arg) => quoteArg(arg)).join(" ")}`;
285→}
286→
287→export function toToolResult(result: CommandExecutionResult, options: ResponseOptions = {}): ToolTextResult {
288→  const timeoutIsExpected = options.timeoutIsExpected ?? false;
289→  const expectedTimeoutSignal =
290→    timeoutIsExpected && result.timedOut && (result.signal === "SIGINT" || result.signal === "SIGTERM");
291→  const isError =
292→    (result.exitCode !== 0 && result.exitCode !== null) ||
293→    (result.timedOut && !timeoutIsExpected) ||
294→    (result.signal !== null && !expectedTimeoutSignal);
295→
296→  const lines: string[] = [
297→    `Executable: ${result.executablePath}`,
298→    `Command: ${commandToString(basename(result.executablePath), result.args)}`,
299→    `Exit code: ${result.exitCode === null ? "null" : String(result.exitCode)}`,
300→    `Duration: ${result.durationMs}ms`,
301→  ];
302→
303→  if (result.signal) {
304→    lines.push(`Signal: ${result.signal}`);
305→  }
306→
307→  if (result.timedOut) {
308→    lines.push(`Timed out: ${timeoutIsExpected ? "expected stop" : "yes"}`);
309→  }
310→
311→  if (options.extraLines && options.extraLines.length > 0) {
312→    lines.push(...options.extraLines);
313→  }
314→
315→  if (result.stdout.length > 0) {
316→    lines.push("", "STDOUT:", result.stdout);
317→  }
318→
319→  if (result.stderr.length > 0) {
320→    lines.push("", "STDERR:", result.stderr);
321→  }
322→
323→  return {
324→    content: [{ type: "text", text: lines.join("\n") }],
325→    isError,
326→  };
327→}
328→
329→async function runCommand(
330→  executable: string,
331→  args: string[],
332→  options?: CommandExecutionOptions,
333→  responseOptions?: ResponseOptions
334→): Promise<ToolTextResult> {
335→  try {
336→    const result = await executeCommand(executable, args, options);
337→    return toToolResult(result, responseOptions);
338→  } catch (error) {
339→    const message = error instanceof Error ? error.message : String(error);
340→    return {
341→      content: [{ type: "text", text: `Error executing ${commandToString(executable, args)}\n${message}` }],
342→      isError: true,
343→    };
344→  }
345→}
346→
347→export async function runSimctl(args: string[], options?: CommandExecutionOptions, responseOptions?: ResponseOptions): Promise<ToolTextResult> {
348→  return await runCommand("xcrun", ["simctl", ...args], options, responseOptions);
349→}
350→
351→export async function runNative(
352→  args: string[],
353→  options?: CommandExecutionOptions,
354→  responseOptions?: ResponseOptions
355→): Promise<ToolTextResult> {
356→  let binary: string;
357→  try {
358→    binary = resolveNativeBinary();
359→  } catch (error) {
360→    return {
361→      content: [{ type: "text", text: error instanceof Error ? error.message : String(error) }],
362→      isError: true,
363→    };
364→  }
365→  return await runCommand(binary, args, options, responseOptions);
366→}
